import React from 'react';
import Calendar from '../lib';

const App = () => (
  <div>
    <Calendar 
      onChange={(val) => {console.log(val)}}
      selectedDate={new Date('Dec 18 2018')}
      selectedMonth={new Date('Dec 18 2018')}
      eventDates={[
        new Date('Dec 13 2018'),
        new Date('Dec 21 2018'),
        new Date('Dec 24 2018')
      ]}
      minDate={new Date('Dec 10 2018')}
      maxDate={new Date('Jan 3 2019')}
      customStyles={{
        header: {},
        title: {},
        prev: {},
        next: {},
        days: {},
        daysRow: {},
        cell: {},
        innerCell: {},
        number: { fontSize: 16, fontWeight: '3400' },
        selected: { background: 'orange' },
        event: { background: 'turquoise', color: 'purple' },
        disabled: { color: 'green'},
        outOfRange: { color: 'red' }
      }}
    />
  </div>
);

export default App;
